$(function () {
    $(".menu__btn-open, .menu__btn-close").click(function () {
        $(".header__top-wrapper").slideToggle();
    });

    function e(e) {
        e.preventDefault();
        for (var t = 0; t < n.length; t++)
            n[t].classList.remove("menu__link--active");
        e.currentTarget.classList.add("menu__link--active");
    }

    for (
        var n = document.getElementsByClassName("menu__link"), t = 0;
        t < n.length;
        t++
    )
        n[t].addEventListener("click", e, !1);

    $("#menu").on("click", "a", function (e) {
        e.preventDefault();

        var t = $(this).attr("href"),
            n = $(t).offset().top;
        $("body,html").animate({ scrollTop: n }, 1500);
    }),
        new WOW().init(),
        $(".slick").slick({
            autoplay: !0,
            infinite: !0,
            autoplaySpeed: 2500,
            slidesToShow: 4,
            slidesToScroll: 1,
            pauseOnHover: !1,
            prevArrow: ".nav__prev",
            nextArrow: ".nav__next",
            responsive: [
                {
                    breakpoint: 480,
                    settings: { vertical: !0, verticalSwiping: !1, arrows: !1 },
                },
            ],
        }),
        (arrowTop.onclick = function () {
            event.preventDefault();
            var e = $(this).attr("href"),
                t = $(e).offset().top;
            $("body,html").animate({ scrollTop: t }, 1500);
        }),
        window.addEventListener("scroll", function () {
            arrowTop.hidden =
                pageYOffset < document.documentElement.clientHeight;
        });
    var o = $(window);
    o.height();
    function a() {
        var n = o.scrollTop(),
            a = 1.1 * o.height();
        $(".revealOnScroll:not(.animated)").each(function () {
            var e = $(this);
            e.offset().top < n + a &&
                (e.data("timeout")
                    ? window.setTimeout(function () {
                          e.addClass("animated " + e.data("animation"));
                      }, parseInt(e.data("timeout"), 10))
                    : e.addClass("animated " + e.data("animation")));
        }),
            $(".revealOnScroll.animated").each(function (e) {
                var t = $(this).offset().top;
                n + a < t && $(this).removeClass("animated bounceInRight");
            });
    }
    Modernizr.touch && $(".revealOnScroll").addClass("animated"),
        o.on("scroll", a),
        a(),
        $(".contacts__btn").magnificPopup({ type: "inline" });

    function currentAge(birthDay) {
        var container = document.getElementById("header__age");
        var innerText = container.innerText;

        innerText = new Date().getFullYear() - birthDay + " " + innerText;

        container.innerText = "";
        container.append(innerText);
    }

    currentAge(1996);
});
